package accessModifiersPackage;

public class IfElseIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		char grade;
		String message;
		
		if (false) {
			grade = 'A';
			message = "hello";
		}else {
			grade = 'B';
			message = "hello sir";
		}
		
		System.out.println(grade+message);
			

	}

}
